mapper.select:
select * from (select rownum rnum, a.*, b.user_id, c.e_no "BANNER" from (select * from tbl_event
where is_del = 'N' and end_date + 1 > sysdate
where is_del = 'N' and sysdate > end_date + 1
where is_del = 'Y'
) a left join tbl_manager_member b on a.m_no = b.m_no left join tbl_main_event c on a.e_no = c.e_no order by a.e_no desc) where rnum between #{startRow} and #{endRow}

select * from (select rownum rnum, a.*, b.user_id from (select * from tbl_event where e_no in (select * from tbl_main_event) and is_del = 'N'
and end_date + 1 > sysdate
and sysdate > end_date + 1
) a left join tbl_manager_member b on a.m_no = b.m_no order by e_no desc) where rnum between #{startRow} and #{endRow}
