mapper.update:
update tbl_memberinfo set
	user_pw = #{user_pw},
	user_name = #{user_name},
	user_email = #{user_email, jdbcType=VARCHAR},
	user_tel = #{user_tel, jdbcType=VARCHAR}
where user_id = #{user_id}

update tbl_workroom set
	wr_intro = #{wr_intro}
where user_id = #{user_id}

update tbl_memberinfo set
	is_del = 'N'
where user_id = #{user_id}

update tbl_event set
	is_del = 'Y'
where e_no = #{e_no}
