mapper.update:
		update tbl_memberInfo set
			user_pw = #{user_pw},
			user_name = #{user_name},
			user_email = #{user_email, jdbcType=VARCHAR},
			user_tel =  #{user_tel, jdbcType=VARCHAR}
 		where user_id = #{user_id}

		update tbl_memberDetail set
			cate_etc = #{cate_etc}
 		where user_id = #{user_id}
