mapper.select:
select * from 
	(select rownum rnum, a.* from 
		(select * from tbl_story
		order by st_no desc)a)
where rnum between #{startRow} and #{endRow}
and user_id = #{user_id}

mapper.update:
update tbl_story set
	st_c_count = st_c_count + #{count}
where st_no = #{st_no}

update tbl_story set
	st_like_count = st_like_count + #{count}
where st_no = #{st_no}
